mapper.select:
select a.create_time createTime,a.id,a.file_name fileName,a.matter,a.order_id orderId,a.`status`,a.video_cover videoCover,b.order_name orderName,c.need_name needName,c.video_group videoGroup,d.averagePlayTimePerPlay,d.reportCost,d.totalPlay,d.reportShow from th_video_matter a left join th_video_order b on a.order_id = b.id left join th_video_need c on a.order_id = c.order_id left join (select matter_id, truncate(avg(average_play_time_per_play),2) averagePlayTimePerPlay,sum(total_play) totalPlay,sum(report_cost) reportCost,sum(report_show) reportShow from th_video_matter_report
and report_time >= left (#{timeRange},10) and report_time <= right(#{timeRange},10)
group by matter_id) d on a.id = d.matter_id
and c.product_name like concat('%',#{productName},'%')
and c.video_dept = #{videoDept}
and c.video_group = #{videoGroup}
and a.file_name like concat('%', #{fileName}, '%')
and a.create_time >= left (#{timeRange},10) and a.create_time <= right(#{timeRange},10)
${sql}

select count(a.id) from th_video_matter a left join th_video_order b on a.order_id = b.id left join th_video_need c on a.order_id = c.order_id left join (select matter_id,avg(average_play_time_per_play) averagePlayTimePerPlay,sum(total_play) totalPlay,sum(report_cost) reportCost,sum(report_show) reportShow from th_video_matter_report group by matter_id) d on a.id = d.matter_id
and c.product_name like concat('%',#{productName},'%')
and c.video_dept = #{videoDept}
and c.video_group = #{videoGroup}
and a.file_name like concat('%', #{fileName}, '%')
and a.create_time >= left (#{timeRange},10) and a.create_time <= right(#{timeRange},10)
